import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import img2 from './assets/img2.jpeg'

import './ImageCarousel.css'; // Add your custom styles if needed

const ImageCarousel = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3, // Set the number of slides to show in a line
        slidesToScroll: 1,
        autoplay: true,      // Enable autoplay
        autoplaySpeed: 3000  // Set the time between slides in milliseconds (adjust as needed)
      };

  return (
    <div className="carousel-container">
      <Slider {...settings}>
        <div>
          <img src="https://placekitten.com/600/400" alt="Slide 1" />
        </div>
        <div>
          <img src={{img2}} alt="Slide 2" />
        </div>
        <div>
          <img src="./assets/img3.png" alt="Slide 3" />
        </div>
        {/* Add more images as needed */}
      </Slider>
    </div>
  );
};

export default ImageCarousel;
